var getValueToDivide = function(maxVal) {
	var num = 1;

	if (maxVal >= 1000000)
		num = 1000000;
	else if (maxVal >= 1000)
		num = 1000;

	return num;
}

// Generate data structure needed for d3Charts
var getNvd3DataJs = function(params, stackedParams) {
	// Check palette is not empty 
	if (params.chartConfigs.mainConfig !== null && params.chartConfigs.mainConfig.hasOwnProperty('colors') && (params.chartConfigs.mainConfig.colors == 'hasTemplate'))
		params.chartConfigs.mainConfig = params.colorPalete[0];

	// Common vars
	var confChartType = params.chartType;
	var confMode = (params.chartConfigs.mainConfig !== null && params.chartConfigs.mainConfig.hasOwnProperty('mode') ? params.chartConfigs.mainConfig['mode'] : 'manual');
	var confColors = checkNumColorsFilter(params.chartConfigs.mainConfig !== null && params.chartConfigs.mainConfig.hasOwnProperty('colors') ? params.chartConfigs['mainConfig']['colors'] : []);
	var confType = (params.chartConfigs.mainConfig !== null && params.chartConfigs.mainConfig.hasOwnProperty('type') ? params.chartConfigs.mainConfig['type'] : 'x');

	if (confChartType != 'parallel')
		var confSteps = (confType == 'y' ? params.chartConfigs['mainConfig']['steps'] : (params.chartLegends == null ? null : params.chartLegends.length));
	else
		var confSteps = (confType == 'y' ? params.chartConfigs['mainConfig']['steps'] : (params.chartValues.mainChart == undefined ? null : params.chartValues.mainChart.length));

	if (typeof window.hasPremiumReportsJsFeatures != 'function') {
		confMode = '';
	}

	var scaleColors = (confMode === 'auto' ? chroma.scale(confColors).mode('lch').colors(confSteps) : confColors);
	scaleColors = checkColorCodeFormat(scaleColors);

	// Axis params
	var y = (params.chartValues.mainChart == undefined ? [] : params.chartValues.mainChart);
	var x = (params.chartLegends == null ? [] : params.chartLegends);
	var numColors = scaleColors.length;
	// Get min & max values
	var minValue = 0;
	var maxValue = 0;
	for (var key in y) {
		var currentValue = parseFloat(y[key]);
		if (currentValue > maxValue) {
			maxValue = currentValue;
		}
		if (currentValue < minValue) {
			minValue = currentValue;
		}
	}

	var sizeSplit = ( ((maxValue-(minValue))+1) / numColors );
	var numToDivide = getValueToDivide(maxValue);

	switch (confChartType) {
	case 'pie':
	case 'donut':
		var chartObject = [];
		y.forEach(function(item, loop) {
			// Cast item to float number
			item = parseFloat(item);
			// Create data array
			var array = {
					key: loop,
					y: item/numToDivide
			}
			// Select palette color (premium / enterprise)
			if (typeof window.hasPremiumReportsJsFeatures == 'function') {
				var colorPaletteUsed = getColor(scaleColors, confMode, confType, item, maxValue, minValue, loop);
			}
			// Add color element to data array
			if (confMode == 'auto') {
				if (confType == 'y') {
					array.color = colorPaletteUsed;
				} else if (confType == 'x') {
					array.color = scaleColors[loop];
				}
			} else { // Manual mode
				array.color = confColors[loop % numColors];
			}

			chartObject.push(array);
		});
		break;

	case 'bar':
		var chartObject = {values:[]};
		y.forEach(function(item, loop) {
			// Cast item to float number
			item = parseFloat(item);
			// Create data array
			var array = {
					label: loop,
					value: item/numToDivide
			}
			// Select palette color (premium / enterprise)
			if (typeof window.hasPremiumReportsJsFeatures == 'function') {
				var colorPaletteUsed = getColor(scaleColors, confMode, confType, item, maxValue, minValue, loop);
			}
			// Add color element to data array
			if (confMode == 'auto') {
				if (confType == 'y') {
					array.color = colorPaletteUsed;
				} else if (confType == 'x') {
					array.color = scaleColors[loop];
				}
			} else { // Manual
				array.color = confColors[loop % numColors];
			}

			chartObject.values.push(array);
		});
		break;

	case "stack":
	case "horizontal":
	case "line":
	case "scatter":
	case "area":
	case "bubble":
		var countGroup = 0;
		var dataGroups = stackedParams.dataGroups[0];
		var chartObject = {values:[]};

		$.each(dataGroups, function(indexGroup, valueGroup) {
			x.forEach(function(valueItem, indexItem) {
				chartObject.values.push({
					x: indexItem,
					y: y[indexItem]/numToDivide
				});
			});
			chartObject.key = indexGroup;
			// Add color element to data array
			if (confMode == 'auto') {
				if (confType == 'y' || confType == 'x') {
					chartObject.color = scaleColors[countGroup%scaleColors.length];
				}
			} else { // Manual mode
				chartObject.color = confColors[countGroup%confColors.length];
			}
			countGroup++;
		});
		break;

	case "parallel":
		var items = params.chartValues.mainChart;
		var chartObject = [];

		for (var i = 0; i < items.length; i++) {
			// Select palette color (premium / enterprise)
			if (typeof window.hasPremiumReportsJsFeatures == 'function') {
				var colorPaletteUsed = getColor(scaleColors, confMode, confType, items[i], maxValue, minValue, i);
			}
			// Create data array
			if (confMode == 'auto') {
				if (confType == 'y') {
					items[i].color = colorPaletteUsed;
				} else if (confType == 'x') {
					items[i].color = scaleColors[i];
				}
			} else { // Manual mode
				items[i].color = confColors[ i % numColors ];
			}
		};
		chartObject.push(items);
		break;
	}

	return chartObject;
}

var checkNumColorsFilter = function(colors) {

	// If no colors defined
	if ((typeof colors === "undefined") || (colors.length == 0) || (typeof window.hasPremiumReportsJsFeatures != 'function')) {
		colors = ["#8c2b2b", "#468c2b", "#2b5d8c", "#cd5200", "#e6bf00", "#7f3acd", "#00a9b8", "#572323", "#004d00", "#000087", "#e48d30", "#9fba09", "#560066", "#009f92", "#b36262", "#38795c", "#3D3D99", "#99623d", "#998a3d", "#994e78", "#3d6899", "#CC0000", "#00CC00", "#0000CC", "#cc5200", "#ccaa00", "#6600cc", "#005fcc"];
	}
	// If some colors
	while (colors.length < 2)
		colors.push(colors[0]);

	return colors;
}

function getUserOptionsReadableFormat(userOptions) {
	
	if ((typeof userOptions == 'undefined') || userOptions === null) {
		userOptions = [];
	}
	
	var ret = "";
	if (userOptions.length > 0) {
		userOptions.forEach(function(entry) {
			var key = entry.key;
			var value = entry.value;
			
			ret += key;
			if ((value != undefined) && (value.length > 0) && (value != key)) {
				ret += "=" + value;
			}
			ret += ",";
			
		});
	}
	
	return ret.slice(0, -1);
}

function getUserOptionsObject(userOptions) {

	var returnedUserOptions = [];
	
	if (userOptions.length > 0) {
		userOptions.split(",").forEach(function(entry) {
			var userOption = {};
			var currentOptionArray = entry.split("=");
			userOption.key = currentOptionArray[0];
			userOption.value = ((currentOptionArray.length > 1) ? currentOptionArray[1] : null);

			returnedUserOptions.push(userOption);
			
		});
	}
	
	return returnedUserOptions;
	
}

function fixUpFilterType(filterType) {

	if (filterType.indexOf("int") === 0)
		return "int";
	else if (filterType.indexOf("decimal") === 0)
		return "decimal";
	else if (filterType.indexOf("float") === 0)
		return "float";
	
	return filterType;
	
}



