var duplicateReferences = function(references){
	
	var errorMessage = SUGAR.language.get('asol_Reports', 'LBL_REPORTS_DUPLICATED_REFERENCES');
	
	var totalElements = references.map(function() { return $(this).val(); }).get();
	var uniqueElements = $.unique(references.map(function() { return $(this).val(); }).get());
	
	var hasDuplicated = (totalElements.length > uniqueElements.length)
	if (hasDuplicated) {
		var ord = totalElements.slice().sort();
		$(ord).each(function(i){if(this==ord[i-1]){
			var match=this;
			references.each(function(){
				if(this.value==match) $(this).css('color', 'orange');
			})
		}});
		$(".referencesErrorMessage").html(errorMessage);
	}
	
	return hasDuplicated;
}

