function getKeyUsageValue(currentKey) {
	
	var usedKeys = [];
	currentKey.split(";").forEach(function(currentKey, keyIndex) {
		var keyArray = currentKey.split(" ");
		usedKeys.push((keyArray.length == 3) ? keyArray[0] : currentKey); 
	});
	
	return usedKeys.join('::');
	
}
	
